import { uploadDocument } from '../services/api';
import React, { useState } from 'react';

const Upload = () => {
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);

  const maxFileSize = 10 * 1024 * 1024; // 10MB
  const allowedTypes = [
    'application/pdf',
    'application/msword',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
    'text/plain'
  ];

  const handleFileChange = (e) => {
    setError(null);
    const selectedFiles = Array.from(e.target.files);
    const validFiles = selectedFiles.filter(file => {
      if (!allowedTypes.includes(file.type)) {
        setError(`File type not allowed: ${file.name}`);
        return false;
      }
      if (file.size > maxFileSize) {
        setError(`File too large (max 10MB): ${file.name}`);
        return false;
      }
      return true;
    });
    setFiles(prev => [...prev, ...validFiles]);
  };

  const handleRemoveFile = (index) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleStartUpload = () => {
    // TODO: Implement API call to upload files
    setUploading(true);
    setTimeout(() => {
      setUploading(false);
      alert('Upload simulation complete');
    }, 2000);
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Upload Documents</h2>
      <div
        className="border-2 border-dashed border-gray-300 rounded-lg p-6 mb-4 text-center cursor-pointer hover:border-primary-600 transition-colors"
        onClick={() => document.getElementById('fileInput').click()}
      >
        <p className="text-gray-500">Drag and drop files here or click to select</p>
        <input
          id="fileInput"
          type="file"
          multiple
          className="hidden"
          onChange={handleFileChange}
          accept=".pdf,.doc,.docx,.txt"
        />
      </div>
      {error && <p className="text-red-600 mb-2">{error}</p>}
      <ul className="mb-4">
        {files.map((file, index) => (
          <li key={index} className="flex justify-between items-center border p-2 rounded mb-2">
            <div>
              <p className="font-medium">{file.name}</p>
              <p className="text-sm text-gray-500">{(file.size / 1024).toFixed(2)} KB</p>
            </div>
            <button
              className="text-red-600 hover:text-red-800"
              onClick={() => handleRemoveFile(index)}
            >
              Remove
            </button>
          </li>
        ))}
      </ul>
      <button
        className="btn-primary"
        onClick={handleStartUpload}
        disabled={files.length === 0 || uploading}
      >
        {uploading ? 'Uploading...' : 'Start Analysis'}
      </button>
    </div>
  );
};

export default Upload;
