import React, { useState, useEffect } from 'react';
import { analyzeDocument, getTaskStatus, getDocuments, exportAnalysis } from '../services/api';

const Analysis = () => {
  const [documents, setDocuments] = useState([]);
  const [selectedDocument, setSelectedDocument] = useState('');
  const [analysisResult, setAnalysisResult] = useState(null);
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await getDocuments();
      setDocuments(response.data.documents);
    } catch (err) {
      setError('Failed to fetch documents');
    }
  };

  const handleAnalyze = async () => {
    if (!selectedDocument) {
      setError('Please select a document');
      return;
    }
    setError('');
    setAnalysisResult(null);
    setStatus('Starting analysis...');
    try {
      const response = await analyzeDocument(selectedDocument);
      setTaskId(response.data.task_id);
      setStatus('Processing...');
      pollTaskStatus(response.data.task_id);
    } catch (err) {
      setError('Failed to start analysis');
    }
  };

  const pollTaskStatus = (id) => {
    const interval = setInterval(async () => {
      try {
        const response = await getTaskStatus(id);
        const task = response.data;
        setStatus(task.state);
        if (task.state === 'SUCCESS') {
          setAnalysisResult(task.result);
          clearInterval(interval);
        } else if (task.state === 'FAILURE') {
          setError('Analysis failed');
          clearInterval(interval);
        }
      } catch (err) {
        setError('Failed to check task status');
        clearInterval(interval);
      }
    }, 1000);
  };

  const isValidHeading = (heading) => {
    if (!heading || typeof heading !== 'string') return false;
    const trimmed = heading.trim();
    if (trimmed.length === 0) return false;
    
    // Reject pure numbers or numbers with dots/periods
    if (/^[\d\s.]+$/.test(trimmed)) return false;
    
    // Reject generic/placeholder text
    const lower = trimmed.toLowerCase();
    if (['clause', 'unnamed', 'unnamed clause', 'undefined', 'null', 'n/a', 'na'].includes(lower)) return false;
    if (lower.startsWith('clause ') && lower.length < 15) return false;
    
    // Must be at least 3 characters and contain at least one letter
    if (trimmed.length < 3 || !/[a-zA-Z]/.test(trimmed)) return false;
    
    return true;
  };

  const renderIdentifiedClauses = () => {
    const mergedClauses = [];
    
    analysisResult.analysis.clauses.forEach((clause) => {
      let heading = null;
      let content = '';
      
      if (typeof clause === 'string') {
        content = clause.trim();
      } else if (clause && typeof clause === 'object') {
        content = (clause.content || '').trim();
        if (isValidHeading(clause.heading)) {
          heading = clause.heading.trim();
        }
      }
      
      // Skip empty content
      if (!content) return;
      
      if (heading) {
        // Valid heading - create new clause block
        mergedClauses.push({ heading, content });
      } else {
        // No valid heading - merge with previous or create standalone
        if (mergedClauses.length > 0 && !mergedClauses[mergedClauses.length - 1].heading) {
          // Merge with previous unheaded content
          mergedClauses[mergedClauses.length - 1].content += '\n\n' + content;
        } else {
          // Create new unheaded block
          mergedClauses.push({ heading: null, content });
        }
      }
    });

    if (mergedClauses.length === 0) {
      return (
        <div className="text-center py-8 text-gray-500">
          <p>No standard clauses identified in this document.</p>
        </div>
      );
    }

    return (
      <div className="space-y-4">
        {mergedClauses.map((block, index) => (
          <div key={index} className="border rounded-lg p-4 bg-gray-50">
            {block.heading && (
              <h4 className="font-semibold text-base text-gray-800 mb-2">
                {block.heading}
              </h4>
            )}
            <div className="text-sm text-gray-700 leading-relaxed whitespace-pre-wrap">
              {block.content}
            </div>
          </div>
        ))}
      </div>
    );
  };

  const handleExportAnalysis = async () => {
    try {
      const response = await exportAnalysis(selectedDocument);
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `analysis_report_${selectedDocument}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      setError('Failed to export analysis');
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-6">Analysis Results</h2>

      {/* Document Selection */}
      <div className="card mb-6">
        <h3 className="text-lg font-medium mb-4">Select Document</h3>
        <select
          value={selectedDocument}
          onChange={(e) => setSelectedDocument(e.target.value)}
          className="border rounded p-2 mb-4"
        >
          <option value="">Select a document</option>
          {documents.map((doc) => (
            <option key={doc._id} value={doc._id}>
              {doc.filename}
            </option>
          ))}
        </select>
        <button
          onClick={handleAnalyze}
          disabled={!selectedDocument || status === 'Processing...'}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Analyze Document
        </button>
        {status && <p className="mt-2">{status}</p>}
        {error && <p className="text-red-600 mt-2">{error}</p>}
      </div>

      {analysisResult && (
        <>
          {/* Document Summary */}
          <div className="card mb-6">
            <h3 className="text-lg font-medium mb-4">Document Summary</h3>
            <div className="bg-gray-50 p-4 rounded-md">
              <p className="text-gray-700">{analysisResult.analysis.summary}</p>
            </div>
          </div>

    {/* Identified Clauses */}
          <div className="card mb-6">
            <h3 className="text-lg font-medium mb-4">Identified Clauses</h3>
            {(() => {
              /**
               * STRICT VALIDATION: Only allow descriptive legal clause headings
               * REJECT: All numeric headings, empty strings, placeholders
               */
              const isDescriptiveLegalHeading = (rawHeading) => {
                // Null/undefined check
                if (!rawHeading) return false;
                
                // Type check
                if (typeof rawHeading !== 'string') return false;
                
                const heading = rawHeading.trim();
                
                // Empty string check
                if (heading.length === 0) return false;
                
                // ===== NUMERIC REJECTION RULES =====
                
                // Rule 1: Reject pure numbers with any punctuation
                // Catches: "1", "2", "3.1", "10", "1.", "2)", "(3)", "[4]", "5-6"
                if (/^[\d\s.,;:\-()[\]\/\\]+$/.test(heading)) {
                  console.log(`[REJECTED - Pure Numeric]: "${heading}"`);
                  return false;
                }
                
                // Rule 2: Reject anything starting with a digit
                // Catches: "1 Introduction", "2. Terms", "3a", "10 Definitions"
                if (/^\d/.test(heading)) {
                  console.log(`[REJECTED - Starts with Digit]: "${heading}"`);
                  return false;
                }
                
                // Rule 3: Reject if it ends with only digits after a space/punctuation
                // Catches: "Article 1", "Section 2", "Clause 3"
                if (/\s\d+[\s.,;:\-()]*$/.test(heading)) {
                  console.log(`[REJECTED - Ends with Number]: "${heading}"`);
                  return false;
                }
                
                // ===== LENGTH & CHARACTER REQUIREMENTS =====
                
                // Must be at least 5 characters
                if (heading.length < 5) {
                  console.log(`[REJECTED - Too Short]: "${heading}"`);
                  return false;
                }
                
                // Must contain at least 4 alphabetic characters
                const letterMatches = heading.match(/[a-zA-Z]/g);
                if (!letterMatches || letterMatches.length < 4) {
                  console.log(`[REJECTED - Insufficient Letters]: "${heading}"`);
                  return false;
                }
                
                // Must have at least one word with 4+ consecutive letters
                if (!/[a-zA-Z]{4,}/.test(heading)) {
                  console.log(`[REJECTED - No Valid Word]: "${heading}"`);
                  return false;
                }
                
                // ===== PLACEHOLDER/GENERIC TERM REJECTION =====
                
                const lowerHeading = heading.toLowerCase();
                
                // List of invalid standalone terms
                const invalidStandaloneTerms = [
                  'clause', 'section', 'article', 'paragraph', 'item',
                  'part', 'title', 'heading', 'subsection', 'subparagraph',
                  'unnamed', 'undefined', 'null', 'n/a', 'na', 'none',
                  'blank', 'untitled', 'unknown', 'untitled clause'
                ];
                
                if (invalidStandaloneTerms.includes(lowerHeading)) {
                  console.log(`[REJECTED - Invalid Term]: "${heading}"`);
                  return false;
                }
                
                // Reject "Clause/Section/Article + Number" patterns
                if (/^(clause|section|article|paragraph|part|item)\s*[\d\w]*$/i.test(heading)) {
                  console.log(`[REJECTED - Generic Pattern]: "${heading}"`);
                  return false;
                }
                
                // ===== DIGIT RATIO CHECK =====
                
                // Reject if more than 40% of non-space characters are digits
                const nonSpaceChars = heading.replace(/\s/g, '');
                const digitCount = (nonSpaceChars.match(/\d/g) || []).length;
                if (digitCount / nonSpaceChars.length > 0.4) {
                  console.log(`[REJECTED - High Digit Ratio]: "${heading}"`);
                  return false;
                }
                
                // PASSED ALL CHECKS
                console.log(`[ACCEPTED]: "${heading}"`);
                return true;
              };

              // Process and consolidate clauses
              const finalClauses = [];
              let activeClause = null;
              
              // Iterate through all clauses from API response
              (analysisResult.analysis.clauses || []).forEach((clause, index) => {
                let proposedHeading = null;
                let clauseContent = '';
                
                // Handle string clauses (content only, no heading)
                if (typeof clause === 'string') {
                  clauseContent = clause.trim();
                }
                // Handle object clauses (with heading and content properties)
                else if (clause && typeof clause === 'object') {
                  clauseContent = (clause.content || '').trim();
                  
                  // Validate heading if present
                  if (clause.heading && isDescriptiveLegalHeading(clause.heading)) {
                    proposedHeading = clause.heading.trim();
                  }
                }
                
                // Skip empty content entirely
                if (!clauseContent) {
                  console.log(`[SKIPPED - Empty Content at index ${index}]`);
                  return;
                }
                
                // Decision: Create new clause or merge with existing
                if (proposedHeading) {
                  // Valid heading found - create new clause block
                  activeClause = {
                    heading: proposedHeading,
                    content: clauseContent
                  };
                  finalClauses.push(activeClause);
                  console.log(`[NEW CLAUSE]: "${proposedHeading}"`);
                } else {
                  // No valid heading - merge into active clause
                  if (activeClause) {
                    activeClause.content += '\n\n' + clauseContent;
                    console.log(`[MERGED INTO]: "${activeClause.heading || 'Unheaded Block'}"`);
                  } else {
                    // No active clause yet - create unheaded block
                    activeClause = {
                      heading: null,
                      content: clauseContent
                    };
                    finalClauses.push(activeClause);
                    console.log(`[NEW UNHEADED BLOCK]`);
                  }
                }
              });

              // Empty state if no valid clauses found
              if (finalClauses.length === 0) {
                return (
                  <div className="text-center py-8 bg-gray-100 rounded-lg">
                    <p className="text-gray-500 text-base">No standard clauses identified in this document.</p>
                  </div>
                );
              }

              // Render consolidated clauses
              return (
                <div className="space-y-5">
                  {finalClauses.map((clauseBlock, idx) => (
                    <div 
                      key={idx} 
                      className="border-2 border-gray-200 rounded-lg p-5 bg-white shadow-sm hover:shadow-md transition-shadow"
                    >
                      {clauseBlock.heading && (
                        <h4 className="font-bold text-lg text-blue-800 mb-4 pb-2 border-b-2 border-blue-200">
                          {clauseBlock.heading}
                        </h4>
                      )}
                      <div className="text-sm text-gray-800 leading-relaxed whitespace-pre-wrap">
                        {clauseBlock.content}
                      </div>
                    </div>
                  ))}
                </div>
              );
            })()}
          </div>

          {/* Clause Classification Summary */}
          <div className="card mb-6">
            <h3 className="text-lg font-medium mb-4">Clause Classification Summary</h3>
            {analysisResult.analysis.classification && Object.keys(analysisResult.analysis.classification).length > 0 ? (
              <ul className="list-disc list-inside text-gray-700">
                {Object.entries(analysisResult.analysis.classification).map(([category, percentage]) => (
                  <li key={category} className="capitalize">
                    {category.replace(/_/g, ' ')}: {percentage.toFixed(1)}%
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500">No clause classifications available.</p>
            )}
          </div>

          {/* Risk Assessment */}
          <div className="card mb-6">
            <h3 className="text-lg font-medium mb-4">Risk Assessment</h3>
            <div className="bg-gray-50 p-4 rounded-md">
              <h4 className="font-medium mb-2">Risks</h4>
              <ul className="list-disc list-inside text-sm text-gray-700">
                {analysisResult.analysis.risks.map((risk, index) => (
                  <li key={index}>{risk}</li>
                ))}
              </ul>
            </div>
          </div>

          {/* Export Analysis */}
          <div className="flex justify-end">
            <button
              onClick={handleExportAnalysis}
              className="bg-green-500 text-white px-4 py-2 rounded"
            >
              Export Analysis Report
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Analysis;
